INSERT INTO `%TABLE_PREFIX%xsd` VALUES (44,'ConferencePosterMD','1.0','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\r\n<xsd:schema xmlns:xsd=\"http://www.w3.org/2001/XMLSchema\" elementFormDefault=\"qualified\" attributeFormDefault=\"unqualified\">\r\n	<!--============================================================\r\n\r\n	 ConferencePosterMD: eSpace Metadata Extension Schema for Conference Posters\r\n		\r\n	============================================================-->\r\n	<xsd:element name=\"ConferencePosterMD\" type=\"ConferencePosterMDType\"/>\r\n	<xsd:element name=\"conference\" />   \r\n	<xsd:element name=\"conf_start_date\" />\r\n	<xsd:element name=\"conf_end_date\" />\r\n	<xsd:element name=\"confloc\" />	     \r\n	<xsd:element name=\"conf_details\" />\r\n	<xsd:element name=\"poster_presentation_date\" />        \r\n	<xsd:complexType name=\"ConferencePosterMDType\">\r\n		<xsd:sequence>\r\n			<xsd:element ref=\"conference\" minOccurs=\"0\" maxOccurs=\"unbounded\"/>\r\n			<xsd:element ref=\"conf_start_date\" minOccurs=\"0\" maxOccurs=\"unbounded\"/>\r\n			<xsd:element ref=\"conf_end_date\" minOccurs=\"0\" maxOccurs=\"unbounded\"/>\r\n			<xsd:element ref=\"confloc\" minOccurs=\"0\" maxOccurs=\"unbounded\"/>\r\n			<xsd:element ref=\"conf_details\" minOccurs=\"0\" maxOccurs=\"unbounded\"/>\r\n			<xsd:element ref=\"poster_presentation_date\" minOccurs=\"0\" maxOccurs=\"unbounded\"/>\r\n		</xsd:sequence>\r\n	</xsd:complexType>\r\n</xsd:schema>\r\n','ConferencePosterMD','',''),(24,'Fedora Object XML','1.1','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\r\n<xsd:schema targetNamespace=\"info:fedora/fedora-system:def/foxml#\" xmlns=\"info:fedora/fedora-system:def/foxml#\" xmlns:xsd=\"http://www.w3.org/2001/XMLSchema\" elementFormDefault=\"qualified\" attributeFormDefault=\"unqualified\">\r\n	<!-- the root element for FOXML: digitalObject -->\r\n	<xsd:element name=\"digitalObject\">\r\n		<xsd:annotation>\r\n			<xsd:documentation/>\r\n		</xsd:annotation>\r\n		<xsd:complexTyp